import React, { useState } from 'react';
import axios from 'axios';

function SignUp({ onSignUpSuccess }) {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validation
    const newErrors = {};
    if (!validateEmail(formData.email)) {
      newErrors.email = 'Invalid email address.';
    }
    if (!validatePassword(formData.password)) {
      newErrors.password = 'Password must not contain your name or email and must meet other criteria.';
    }
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    // API Request to register the user
    try {
      const response = await axios.post('http://localhost:8000/api/users', {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        password: formData.password
      });
      onSignUpSuccess(response.data);  // Navigate on success
    } catch (error) {
      if (error.response && error.response.status === 409) {
        setErrors({ email: 'An account with this email already exists.' });
      } else {
        console.error('Signup failed:', error);
        setErrors({ general: 'Failed to create account. Please try again.' });
      }
    }
  };

  return (
    <div className="signup-form">
      <h1>Create Account</h1>
      {errors.general && <p className="error">{errors.general}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          placeholder="First Name"
          required
        />
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          placeholder="Last Name"
          required
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          required
        />
        {errors.email && <p className="error">{errors.email}</p>}
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          required
        />
        {errors.password && <p className="error">{errors.password}</p>}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );

const validateEmail = (email) => {
    return /\S+@\S+\.\S+/.test(email);
  };

    const validatePassword = (password, { firstName, lastName, email } = {}) => {
    if (password.length < 8 || password.includes(firstName) || password.includes(lastName) || password.includes(email)) {
      return false;
    }
    return true;
  };

  }

export default SignUp;
