import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SignIn({ onSignInSuccess }) {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  // Check internet status of User
  useEffect(() => {
    function updateOnlineStatus() {
      setIsOnline(navigator.onLine);
    }

    window.addEventListener('online', updateOnlineStatus);
    window.addEventListener('offline', updateOnlineStatus);

    return () => {
      window.removeEventListener('online', updateOnlineStatus);
      window.removeEventListener('offline', updateOnlineStatus);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});

    // Check if User is online
    if (!isOnline) {
      setErrors({ general: 'No internet connection. Please connect and try again.' });
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/users/login', formData, {
        withCredentials: true
      });
      onSignInSuccess(response.data);  // Navigate on success or handle session storage etc.
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setErrors({ general: 'Invalid email or password.' });
      } else {
        console.error('Login failed:', error);
        setErrors({ general: 'An error occurred. Please try again.' });
      }
    }
  };

  return (
    <div className="signin-form">
      <h1 className="signin-title">Sign In</h1>
      {errors.general && <p className="error">{errors.general}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Email"
          required
          disabled={!isOnline}
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          required
          disabled={!isOnline}
        />
        <button type="submit" disabled={!isOnline}>Log In</button>
      </form>
      {!isOnline && <p className="error-message">Internet connection is required to log in.</p>}
    </div>
  );
}

export default SignIn;
