import React from 'react';

function Sidebar({ onQuestionsSidebarClick, onTagsSidebarClick, onLogoutClick, activeSection }) {

    return (
        <div className="sidebar">
            <ul>
                <li><button className={`menu-item ${activeSection === 'questions' ? 'active' : ''}`}
                    onClick={onQuestionsSidebarClick}
                >Questions</button></li>
                <li><button className={`menu-item ${activeSection === 'tags' ? 'active' : ''}`}
                    onClick={onTagsSidebarClick}
                >Tags</button></li>
            </ul>
            <div className="logout-container">
                <button className="logout-button" onClick={onLogoutClick}>Logout</button>
            </div>
        </div>
    );
}

export default Sidebar;
