import React from 'react';

function Sidebar({ onQuestionsSidebarClick, onTagsSidebarClick, onProfileClick, onLogoutClick, activeSection }) {

    return (
        <div className="sidebar">
            <ul>
                <li><button className={`menu-item ${activeSection === 'questions' ? 'active' : ''}`}
                    onClick={onQuestionsSidebarClick}
                >Questions</button></li>
                <li><button className={`menu-item ${activeSection === 'tags' ? 'active' : ''}`}
                    onClick={onTagsSidebarClick}
                >Tags</button></li>
                <li><button className={`menu-item ${activeSection === 'profile' ? 'active' : ''}`}
                    onClick={onProfileClick}>
                    Profile
                </button></li>
                <li><button className="menu-item" onClick={onLogoutClick}>Logout</button></li>
            </ul>
        </div>
    );
}

export default Sidebar;
