import React, { useState } from 'react';
import axios from 'axios';

function SignUp({ onSignUpSuccess }) {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Reset errors
    setErrors({});

    // Validate email and password
    if (!validateEmail(formData.email)) {
      setErrors({ email: 'Invalid email address.' });
      return;
    }
    if (!validatePassword(formData.password, formData)) {
      setErrors({ password: 'Password must not contain your name or email and must meet other criteria.' });
      return;
    }

    // API Request to register the user
    try {
      const response = await axios.post('http://localhost:8000/api/users', formData);
      onSignUpSuccess(response.data);  // Navigate on success
    } catch (error) {
      if (error.response && error.response.data.message) {
        setErrors({ general: error.response.data.message });
      } else {
        console.error('Signup failed:', error);
        setErrors({ general: 'Failed to create account. Please try again.' });
      }
    }
  };

  return (
    <div className="signup-form">
      <h1>Create Account</h1>
      {errors.general && <p className="error">{errors.general}</p>}
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

const validateEmail = (email) => {
  return /\S+@\S+\.\S+/.test(email);
};

const validatePassword = (password, { firstName, lastName, email }) => {
  if (password.length < 8 || password.includes(firstName) || password.includes(lastName) || password.includes(email.split('@')[0])) {
    return false;
  }
  return true;
};

export default SignUp;
