import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile({ user = null, onEditQuestion }) {
  const [profile, setProfile] = useState(null);
  const [errors, setErrors] = useState('');

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  useEffect(() => {
    if (user === null) {
      axios.get('http://localhost:8000/users/profile', { withCredentials: true })
      .then(response => {
        setProfile(response.data);
      })
      .catch(error => {
        console.error('Error fetching user profile:', error);
        setErrors('Failed to load profile');
      });
    }
    else {
      setProfile(user);
    }
  }, [user]);

  // Handling loading state
  if (!profile && !errors) {
    return <div>Loading...</div>;
  }

  // Handling error state
  if (errors) {
    return <div>Error: {errors}</div>;
  }

  // Main content when data is loaded and there are no errors
  return (
    <div>
      <h1>User Profile</h1>
      <p>Username: {profile?.username}</p>
      <p>Member since: {formatDate(profile?.memberSince)}</p>
      <p>Reputation: {profile?.reputation}</p>
      <div>
        <h2>My Questions</h2>
        {profile.questions && profile.questions.length > 0 ? (
          profile.questions.map(question => (
            <div key={question.id} onClick={() => onEditQuestion(question)}>
              <a href={`/questions/${question.id}`}>{question.title}</a>
            </div>
          ))
        ) : <p>No questions posted yet.</p>}
      </div>
      <div>
        <h2>My Answers</h2>
        {profile.answers && profile.answers.length > 0 ? (
          profile.answers.map(answer => (
            <div key={answer._id}>
              <a href={`/answers/${answer._id}`}>{answer.text}</a> 
              <p>Posted on: {formatDate(answer.ans_date_time)}</p>
            </div>
          ))
        ) : <p>No answers posted yet.</p>}
      </div>
    </div>
  );
}

export default UserProfile;
