import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [profile, setProfile] = useState(null);
  const [errors, setErrors] = useState('');

  // Make date look pretty
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  useEffect(() => {
    // axios.get('http://localhost:8000/users/profile', { withCredentials: true })
    //   .then(response => {
    //     setProfile(response.data);
    //   })
    //   .catch(error => {
    //     console.error('Error fetching user profile:', error);
    //     setErrors('Failed to load profile');
    //   });
    const fetchUserData = async () => {
      try {
          const response = await axios.get('http://localhost:8000/users/profile', { withCredentials: true });
          setProfile(response.data);
      } catch (error) {
          console.error('Error fetching user data:', error);
          setErrors('Failed to load profile');
      }
  };

  fetchUserData();

  }, []);

  // Handling loading state
  if (!profile && !errors) {
    return <div>Loading...</div>;
  }

  // Handling error state
  if (errors) {
    return <div>Error: {errors}</div>;
  }

  // Main content when data is loaded and there are no errors
  return (
    <div>
      <h1>User Profile</h1>
      <p>Member since: {formatDate(profile?.createdAt)}</p>
      <p>Reputation: {profile?.reputation}</p>
      <div>
        <h2>My Questions</h2>
        {profile.questions && profile.questions.length > 0 ? (
          profile.questions.map(question => (
            <div key={question._id}>
              <a href={`/questions/${question.id}`}>{question.title}</a>
            </div>
          ))
        ) : <p>No questions posted yet.</p>}
      </div>
      <div>
        <h2>My Tags</h2>
        {profile.tags && profile.tags.length > 0 ? (
          profile.tags.map(tag => (
            <div key={tag.id}>
              <a href={`/tags/${tag.id}`}>{tag.title}</a>
            </div>
          ))
        ) : <p>No tags posted yet.</p>}
      </div>
      <div>
        <h2>My Answers</h2>
        {profile.answers && profile.answers.length > 0 ? (
          profile.answers.map(answer => (
            <div key={answer.id}>
              <a href={`/answers/${answer.id}`}>{answer.title}</a>
            </div>
          ))
        ) : <p>No answers posted yet.</p>}
      </div>
    </div>
  );
}

export default UserProfile;
